import React from "react";
import Button from "./Button";
import InputBox from "./InputBox";
const Form = ()=>{
    return (
        <form className="form">
            <div className="input-group">
                <InputBox type={"number"} id={"current-savings"} name={"Current Savings ($)"}/>
                <InputBox type={"number"} id={"yearly-contribution"} name={"Yearly Savings ($)"}/>
            </div>
            <div className="input-group">
                <InputBox type={"number"} id={"expected-return"} name={"Expected Interest (%, per year)"}/>
                <InputBox type={"number"} id={"duration"} name={"Investment Duration (years)"}/>
            </div>
            <p className="actions">
                <Button action={"submit"} name={"Calculate"} className={"button"}/>
                <Button action={"reset"} name={"Reset"} className={"buttonAlt"}/>
            </p>
        </form>
    );
}

export default Form
