import React, {useState} from "react";
import Button from "./Button";
const Form = ()=>{
   const[data,setData] =  useState({
       currentSaving:"",
       yearlySaving:"",
       expectedInterest:"",
       investmentDuration:""
   })

    const currentSavingHandler = event => {
        setData((prevState) => {
            return {...prevState, currentSaving: event.target.value}
        })

    }

    const yearlySavingHandler = event => {
        setData((prevState) => {
            return {...prevState, yearlySaving: event.target.value}
        })


    }

    const expectedInterestHandler = event => {
        setData((prevState) => {
            return {...prevState, expectedInterest: event.target.value}
        })


    }


    const investmentDurationHandler = event => {
        setData((prevState) => {
            return {...prevState, investmentDuration: event.target.value}
        })


    }

    return (
        <form className="form">
            <div className="input-group">
                <p>
                    <label htmlFor="current-savings">Current Savings ($)</label>
                    <input type="number" id="current-savings" onChange={currentSavingHandler} />
                </p>
                <p>
                    <label htmlFor="yearly-contribution">Yearly Savings ($)</label>
                    <input type="number" id="yearly-contribution"  onChange={yearlySavingHandler}/>
                </p>
            </div>
            <div className="input-group">
                <p>
                    <label htmlFor="expected-return">
                        Expected Interest (%, per year)
                    </label>
                    <input type="number" id="expected-return"  onChange={expectedInterestHandler}/>
                </p>
                <p>
                    <label htmlFor="duration">Investment Duration (years)</label>
                    <input type="number" id="duration"  onChange={investmentDurationHandler}/>
                </p>
            </div>
            <p className="actions">
                <Button action={"submit"} name={"Calculate"} className={"button"}/>
                <Button action={"reset"} name={"Reset"} className={"buttonAlt"}/>
            </p>
        </form>
    );
}

export default Form
